function formatTime(time) {
	if (time.length>0) {
		var hr = parseInt(time.split(":")[0]);
		var fhr = (hr > 12) ? hr-12 : hr;
		var ap = (hr < 12) ? 'am' : 'pm';
		return fhr+":"+time.split(":")[1]+ap;
	} else {
		return ""
	}
}